grid[3][30] = 2
grid[36][32] = 2


puntos = {'(51, 64)': "Entrada Principal", '(3, 30)': "Entrada Ceibas", '(36, 32)': "Entrada CDl"}
# Establecemos el margen entre las celdas.
MARGEN = 1

file = open("map.txt","w")

pygame.init()
size = (1200,620)
screen = pygame.display.set_mode(size)
pygame.display.set_caption("Mapa Javeriana A*")
clock = pygame.time.Clock()
background_image = load_image('FondoN.jpg')
gameOver = False
myfont = pygame.font.SysFont("monospace", 15)
label = myfont.render("!camino a la U!", 30, (255,255,0))
intru = myfont.render("Seleccione origen y destino:", 30, (255,255,0))
origenT = myfont.render("Origen:", 30, (255,255,0))
destinoT = myfont.render("Destino:", 30, (255,255,0))
oring_a = myfont.render("", 30, (255,255,0))
desting_a = myfont.render("", 30, (255,255,0))
note = myfont.render("", 30, (255,255,0))
origen = 0
destino = 0

def limpiar(grid):
    for i in range(len(grid)):
        for j in range(len(grid[i])):
            if(grid[i][j] == 3):
                grid[i][j] = 1
    return grid

while not gameOver:
    for event in pygame.event.get():
        if event.type == pygame.QUIT:
            gameOver = True
        elif event.type == pygame.KEYDOWN and event.key == pygame.K_RETURN:
            grid = limpiar(grid)
            if(origen == destino):
                note = myfont.render("lugares iguales", 30, (255,255,0))
            elif(origen == 0 or destino == 0):
                note = myfont.render("falta un dato", 30, (255,255,0))
            else:
                 note = myfont.render("", 30, (255,255,0))
                 grid[origen[0]][origen[1]] = 1
                 grid[destino[0]][destino[1]] = 1
                 twoGrid = convertir(grid)
                 route = astar(twoGrid,origen,destino)
                 route = route + [origen]
                 route = route[::-1]
                 for i in range(1,len(route)-1):
                     grid[route[i][0]][route[i][1]] = 3
                 grid[origen[0]][origen[1]] = 2
                 grid[destino[0]][destino[1]] = 2
                
        elif event.type == pygame.MOUSEBUTTONDOWN:
            # El usuario presiona el ratón. Obtiene su posición.
            pos = pygame.mouse.get_pos()
            columna = pos[0] // (LARGO + MARGEN)
            fila = pos[1] // (ALTO + MARGEN)
            print("Click ", pos, "Coordenadas de la retícula: ", fila, columna, grid[fila][columna])
            if(columna<=84 and fila<=55):

                if(event.button == 1 and grid[fila][columna] == 2):
                    origen = (fila,columna)
                    oring_a = myfont.render(str(puntos[str((fila,columna))]), 30, (255,255,0))
                elif(event.button == 3 and grid[fila][columna] == 2):
                    destino = (fila,columna)
                    desting_a = myfont.render(str(puntos[str((fila,columna))]), 30, (255,255,0))
                   


    screen.fill(Gris)
    screen.blit(background_image,(0,0))

    screen.blit(label, (1000, 100))
    screen.blit(intru, (950, 200))
    screen.blit(origenT, (950, 300))
    screen.blit(destinoT, (950, 400))
    screen.blit(oring_a, (1030, 300))
    screen.blit(desting_a, (1030, 400))
    screen.blit(note, (1030, 500))



    contx = 0
    conty =0
    for fila in range(56):
        for columna in range(85):
            color = BLACK
            if grid[fila][columna] == 1:
                color = azul
                pygame.draw.rect(screen, color,[(MARGEN+LARGO) * columna + MARGEN,
                                  (MARGEN+ALTO) * fila + MARGEN,
                                  LARGO,
                                  ALTO])
            elif grid[fila][columna] == 2:
                color = redP
                pygame.draw.rect(screen, color,[(MARGEN+LARGO) * columna + MARGEN,
                                  (MARGEN+ALTO) * fila + MARGEN,
                                  LARGO,
                                  ALTO])
            elif grid[fila][columna] == 3:
                color = greenP
                pygame.draw.rect(screen, color,[(MARGEN+LARGO) * columna + MARGEN,
                                  (MARGEN+ALTO) * fila + MARGEN,
                                  LARGO,
                                  ALTO])

            else:
                pygame.draw.rect(screen, color,[(MARGEN+LARGO) * columna + MARGEN,
                                  (MARGEN+ALTO) * fila + MARGEN,
                                  LARGO,
                                  ALTO],1)

    pygame.display.flip()
    clock.tick(60)
pygame.quit()
